import React, { useState } from "react";
import Footer from "./Footer/Footer";
import Header from "./Header/Header";
import './index.css';
import Items from "./Items/Items";

const addToOrder = (item) => {
    setOrders([...orders, item]);
}

export default function App() {

    const [items, setItems] = useState([
        {
            id: 1,
            title: 'Стілець сірий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '49.99'
        },
        {
            id: 2,
            title: 'Стілець чорний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '59.99'
        },
        {
            id: 3,
            title: 'Стілець білий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '69.99'
        },
        {
            id: 4,
            title: 'Стілець червоний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '79.99'
        },
        {
            id: 5,
            title: 'Стілець зелений',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '89.99'
        }
    ]);
    const [orders, setOrders] = useState([]);

    return (
        <div className="wrapper">
            <Header />
            <Items items={items}/>
            <Footer />
        </div>
    );
}
