import React, { useState } from "react";
import Footer from "./Footer/Footer";
import Header from "./Header/Header";
import './index.css';
import Items from "./Items/Items";

export default function App() {

    const [items, setItems] = useState([
        {
            id: 1,
            title: 'Стілець сірий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '49.99'
        },
        {
            id: 2,
            title: 'Стілець чорний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '59.99'
        },
        {
            id: 3,
            title: 'Стілець білий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '69.99'
        },
        {
            id: 4,
            title: 'Стілець червоний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '79.99'
        },
        {
            id: 5,
            title: 'Стілець зелений',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '89.99'
        }
    ]);

    const [orders, setOrders] = useState([]);

    const addToOrder = (item) => {
        // Перевіряємо, чи елемент вже є в корзині
        const isInArray = orders.some(el => el.id === item.id);
    
        // Якщо елемент ще не додано до корзини, додаємо його
        if (!isInArray) {
            setOrders([...orders, item]);
        }
    }

    return (
        <div className="wrapper">
            <Header orders={orders} />
            <Items items={items} onAdd={addToOrder}/>
            <Footer />
        </div>
    );
}
