export default function Item({item, onAdd}) {

return (
    <div className="item">
        <img src={'./img/' + item.img} className="item_img"/>
        <h2 className="item_title">{item.title}</h2>
        <p className="item_list">{item.descr}</p>
        <p className="item_price">{item.price}$</p>
        <button className="add-to-card" onClick={() => {onAdd(item)}}>+</button>
    </div>
)

}
