import styled from "styled-components";

export const HeaderLogo = styled.div`
    font-weight: 600;
    font-size: 24px;
`

export const NavigationList = styled.ul`
    display: flex;
    justify-content: right;
    list-style: none;
`

export const NavigationItem = styled.li`
    margin-right: 30px;
    transition: transform 500ms ease;


    &:hover {
        opacity: 0.5;
        transition: transform 500ms ease;
    }
`
