    { 
        id: 5,
        img: 'chair1.jpg',
        title: 'Стілець білий',
        price: 49.99,
        descr: 'lorem lorem lorem lorem lorem ',
        category: 'chair'
    },
    { 
        id: 6,
        img: 'chair1.jpg',
        title: 'Стілець білий',
        price: 49.99,
        descr: 'lorem lorem lorem lorem lorem ',
        category: 'chair'
    },
 
])

const [orders, setOrder] = useState([])

const onAdd = (item) => {

    setOrder([...orders, item])

}

return (
    <div className="container">
        <Header orders={orders}/>
        <Items items={items} onAdd={onAdd}/>
        <Footer/>
    </div>
)

}
