import React, { useState } from "react";
import Footer from "./Footer/Footer";
import Header from "./Header/Header";
import './index.css';
import Items from "./Items/Items";


export default function App() {

    const [items, setItems] = useState([
        {
            id: 1,
            title: 'Стілець сірий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '49.99'
        },
        {
            id: 2,
            title: 'Стілець чорний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '59.99'
        },
        {
            id: 3,
            title: 'Стілець білий',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '69.99'
        },
        {
            id: 4,
            title: 'Стілець червоний',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '79.99'
        },
        {
            id: 5,
            title: 'Стілець зелений',
            img: 'chair1.jpg',
            desc: 'lorem',
            category: 'chairs',
            price: '89.99'
        }
    ]);
    const [orders, setOrders] = useState([]);

    const addToOrder = (itemToAdd) => {
        // Перевіряємо, чи елемент уже є в корзині
        const existingItem = orders.find(item => item.id === itemToAdd.id);
    
        if (existingItem) {
            // Якщо елемент уже є, оновлюємо його кількість або ігноруємо додавання
            const updatedOrders = orders.map(item => {
                if (item.id === existingItem.id) {
                    return { ...item, quantity: item.quantity + 1 }; // Оновлюємо кількість
                }
                return item;
            });
            setOrders(updatedOrders);
        } else {
            // Якщо елементу ще немає в корзині, додаємо його з кількістю 1
            setOrders([...orders, { ...itemToAdd, quantity: 1 }]);
        }
    };
    

    return (
        <div className="wrapper">
            <Header orders={orders}  />
            <Items items={items} onAdd={addToOrder}/>
            <Footer />
        </div>
    );
}
