import { useState } from "react"
import {Category} from './Categories.styled'

export default function Categiries({choseCategory}) {

const [categories, setCategories] = useState([
    {
        key: 'all',
        name: 'Всі'
    },
    {
        key: 'Chairs',
        name: 'Крісла'
    },
    {
        key: 'tables',
        name: 'Столи'
    },
    {
        key: 'sofa',
        name: 'Дивани'
    },

    {
        key: 'light',
        name: 'Світло'
    }

])

return (
    <div>
        {categories.map(el => (
            <Category key={el.id} onClick={() => choseCategory(el.key)}>{el.name}</Category>
        ))}
    </div>
)

}
