import { FaCartShopping } from "react-icons/fa6";
import Order from "../Order/Order";
import { useState } from "react";

export default function Header(orders) {

    const [cartOpen, setCartOpen] = useState(false)

const showOrders = orders => {
    return (
        <div>
            {orders.map(item => (
                <Order key={item.id} item={item}/>
            ))}
        </div>
    )
}

const showNothing = () => {
    return (
        <div>
            Тут пусто
        </div>
    )
}

return (
    <header className="header">
            <div className="logo">House Staf</div>
            <ul className="navList">
                <FaCartShopping onClick={() => {setCartOpen(cartOpen => !cartOpen)}} className={`shop ${cartOpen && 'active'}`}/>
                <li className="navItem">Про нас</li>
                <li className="navItem">Контакти</li>
                <li className="navItem">Кабінет</li>
            </ul>
        
        {cartOpen && (
            <div className="cart-open">
                відкрито
            </div>
        )}

        <div className="presentation">

        </div>
    </header>
)

}
